import React, { useEffect, useState } from "react";
import { useNavigate, useParams } from "react-router-dom";
import OrderItem from "../components/OrderItem";
import { useTypedSelector } from "../hooks/useTypedSelector";
import "./Register.css"
export function Register() {
    const initialState = {
        login: "",
        password: "",
        confirmPassword: "",
        email: "",
    }
    const navigate=useNavigate()
    const [isSucess2,setisSucess2]=useState(false);
    const [formValue, setFormValue] = useState(initialState)
    const { login, password, confirmPassword, email } = formValue
    const handleChange=(e:any)=>{
        console.log(formValue)
        setFormValue({...formValue,[e.target.name]:e.target.value})
      }
      const handleRegister=async ()=>{
        if(login && password && confirmPassword && email){
            //await loginUser({login,password})
            await setisSucess2((prev)=>!prev)       
            console.log(isSucess2)
        }
        else{
        //   toast.error("Fill all fields")
        }
      }
      useEffect(()=>{
    if(isSucess2){
      console.log("true")
      navigate("/home")
    }
  },[isSucess2])
    return (
        <div className="register-wrapper">
            <h3>Регистрация</h3>
            <div className="register-form">
                <div className="credentials_register">
                    <div className="login">
                        <label>Login</label>
                        <input type="login"
                            id='login'
                            className='form-control'
                            name="login"
                            value={login}
                            onChange={handleChange} />
                    </div>
                    <div className="password">
                        <label>Password</label>
                        <input type="password"
                            id='password'
                            className='form-control'
                            name="password"
                            value={password}
                            onChange={handleChange}/>
                    </div>
                    <div className="confirm-password">
                        <label>Confirm Password</label>
                        <input type="password"
                            id='confirm'
                            className='form-control'
                            name="confirmPassword"
                            value={confirmPassword}
                            onChange={handleChange}/>
                    </div>
                    <div className="email">
                        <label>Email</label>
                        <input type="email"
                            id='email'
                            className='form-control'
                            name="email"
                            value={email}
                            onChange={handleChange} />
                    </div>
                    <div className="type-user">
                        <input type="radio" ></input>
                        <input type='radio' ></input>
                    </div>
                </div>
                <div className="button-register">
                    <button>Register</button>
                </div>

            </div>
        </div>
    )
}
