import React, { useEffect, useState } from "react";
import { useGetProductsQuery, useGetStringQuery } from "../store/order/order.api";
import OrderItem from "../components/OrderItem"
import './AllOrders.css'
import isNil from "lodash/isNil";
import isNaN from "lodash/isNaN";
import { Link, Outlet } from "react-router-dom"
import { IOrder, orderType } from "../store/order/order.type";
import { useActions } from "../hooks/useActions";
import { useTypedSelector } from "../hooks/useTypedSelector";
import { Pagination } from "../components/Pagination";
import { useLocation, useNavigate } from "react-router-dom";
import { Modal } from "../components/Modal";
import { Categories } from "../components/Categories";

const initstate: IOrder[] = []
export function AllOrders() {
    const initialState: string[] = []
    const [categories, setCategories] = useState(initialState)
    const { addOrders } = useActions();
    const { orders } = useTypedSelector(state => state);
    const navigate = useNavigate();
    const location = useLocation();
    const pageNumber = !isNaN(Number(location.search.split("=")[1]))
        ? Number(location.search.split("=")[1])
        : 1;
    const [ordersData, SetOrders] = useState(initstate)
    const [pagesCount, setPagesCount] = useState(0);
    const [currentPage, setCurrentPage] = useState(pageNumber);
    const { data, isLoading, error } = useGetProductsQuery(currentPage)
    const handlePageChange = ({ selected }: { selected: number }) => {
        setCurrentPage(selected + 1);
        console.log(pageNumber)
        console.log("changing")
        // history.replace(`?page=${selected + 1}`);
        navigate(`/allOrders/?page=${selected + 1}`)
    };

    // const [isLoading,error]:boolean[]=[false,true]
    const categoriesHandler = (event: any) => {
        if (!categories.includes(event.target.name))
            setCategories([...categories, event.target.name])
        else {
            setCategories((prev) => {
                return prev.filter(e => e !== `${event.target.name}`)
            })
        }
    }

    const test = () => {
        console.log(categories)
    }
    const submitHadnler = () => {

    }
    useEffect(() => {
        // const fetchPr = async () => {
        //     const tempdata: IOrder[] = orderstemp.slice((currentPage - 1) * 3, (currentPage) * 3)
        //     console.log(tempdata + " slice")
        //     console.log(orderstemp +"temp")
        //     await setOrders(tempdata)
        // }
        if (!isLoading) {
            const pagesQuantity = data && Math.max(Math.ceil(data?.TotalCountItems / data?.ItemsPerPage), 1)
            // console.log(data?.TotalCountItems + "total")
            // console.log(data?.ItemsPerPage + "perpage")
            // console.log(data?.orders)
            // console.log(pagesQuantity + "pagesQuantity")
            // if(categories.length>0) {
            //     const data:IOrder[]=[{
            //         id: 1,
            //         title: "wtf",
            //         description: "",
            //         cost: 1234,
            //         period: "asda",
            //     },{
            //         id: 1,
            //         title: "wtf",
            //         description: "",
            //         cost: 1234,
            //         period: "asda",
            //     },{
            //         id: 1,
            //         title: "wtf",
            //         description: "",
            //         cost: 1234,
            //         period: "asda",
            //     },{
            //         id: 1,
            //         title: "wtf",
            //         description: "",
            //         cost: 1234,
            //         period: "asda",
            //     },{
            //         id: 1,
            //         title: "wtf",
            //         description: "",
            //         cost: 1234,
            //         period: "asda",
            //     },{
            //         id: 1,
            //         title: "wtf",
            //         description: "",
            //         cost: 1234,
            //         period: "asda",
            //     },{
            //         id: 1,
            //         title: "wtf",
            //         description: "",
            //         cost: 1234,
            //         period: "asda",
            //     },]
            //     setOrders(data)
            // }

            // fetchPr()
            // const tempdata: IOrder[] = orderstemp.slice((currentPage - 1) * 3, (currentPage) * 3)
            // console.log(tempdata + " slice")

            // setOrders(tempdata)

            if (data) {
                addOrders(data.orders as IOrder[])
            }
            const fetchPr = async () => {
                await SetOrders(data?.orders as IOrder[])
            }
            fetchPr();
            console.log(ordersData + "ordersData")
            if (pagesQuantity) {
                setPagesCount(pagesQuantity);
            }
            console.log(currentPage + "currentPage")
            console.log(categories + "categories")
        }
    }, [currentPage, categories, isLoading])
    return (
        <div className="all-container">
            <div className="left-container">
                Left
                <div className="filter-container">
                    {/* <div className="filter-header">
                        Выбрать критерии
                    </div>
                    <ul>
                        <input type="checkbox" onClick={categoriesHandler}></input>
                        <label >Anime</label>
                    </ul> */}
                    <Categories categoriesHandler={categoriesHandler} submitHandler={submitHadnler} />
                </div>
            </div>
            {/* <div className='head-container'>
                <h3>Все доступные заказы</h3>
            </div> */}
            <div className="orders-container">
                <h4 className="orders-header">Все заказы</h4>
                {/* <Modal/> */}
                {isLoading ? (
                    'Loading...'
                ) : error ? (
                    <div>Error</div>
                ) : (
                    <div className='flex flex-wrap justify-between'>
                        {data?.orders && data.orders.map(order => (
                            (categories.length == 0 || categories.includes(order.title)) &&
                            <OrderItem key={order.id} order={order} />
                        ))}
                    </div>
                )}
                <Pagination
                    initialPage={1}
                    pagesCount={5}
                    onChange={handlePageChange} />
                <button onClick={test}>ASD</button>
            </div>

        </div>
    )
}
