import React from 'react'
import { FC,useState } from 'react'
import { IOrder } from "../store/order/order.type"
import "./OrderItem.css"
// import 'bootstrap/dist/css/bootstrap.min.css';
import { Link, Outlet } from "react-router-dom"
import { EditModal } from './EditModal'
import { Modal } from './Modal'
import { EditForm } from './EditForm'
import { useGetUserByIdQuery } from '../store/auth/auth.api'
// const OrderItem: FC<{ order: IOrder }> = ({ order }) => {

export interface IOrderItemProps{
	order:IOrder,
	auth?:{id:number |null;
		name:string | null;
		token:string | null;
	}
	deleteOrder?:(id:number)=>void;
	editOrder?:(id:number,title:string,description:string,cost:number,topic:string)=>void;
}
const initialState={
	title:"",
	description:"",
	cost:"",
	topic:""
}
const OrderItem: FC<IOrderItemProps> = ({ order,auth,deleteOrder,editOrder }) => {
	const [editModal,setEditModal]=useState(false)
	const [formValue,setFormValue]=useState(initialState)
	const {title,description,cost,topic}=formValue
	const {data,error}=useGetUserByIdQuery(order.creatorId)
	const closemodal=()=>{
		setEditModal(false)
	}
	const showmodal=()=>{
		setEditModal(true)
	}

	const handleChange = (e: any) => {
		setFormValue({ ...formValue, [e.target.name]: e.target.value })
	  }
	return (
		<div
			className='order-container'
		>
			<div className="order-content">
				<div className='title-cost'>
					<Link to={`/allorders/${order.id}`} className="nav-link">
						<h2>{order.title}</h2></Link>
					<h3>{order.cost +"₽"}</h3>
				</div>
				<div className="order-description">
					<p>{order.description}</p>
				</div>
				<div className="order-credentials">
					<div className='order-topic'>{"Категория:"+order.topic}</div>
					<div className='order-date'>{"Срок выполнения:"+order.period}</div>
					<div className='creator-email'> {"Обращаться на почту:"+data?.email}</div>
				</div>
				{auth && deleteOrder  && <div className='crud-buttons'>
					<button className="order-delete-button" onClick={()=>deleteOrder(order.id)}>Delete</button>
					<button className="order-edit-button" onClick={()=>setEditModal(true)}	>Edit</button>
				</div>}
				{editModal && <EditForm show={editModal} handleClose={closemodal} handleShow={showmodal} />}
				{/* {editModal && <Modal modalActive={editModal} setModalActive={setEditModal} 
				
				children={
					<div className='modal-wrapper'>
						<form className='modal-form'>
							<div className='edit-title'>
								<label>Title</label>
								<input id='title' name='title' value={title} onChange={handleChange}></input>
							</div>
							<div className='edit-description'>
								<label>Description</label>
								<input id='description' name='description' value={description} onChange={handleChange}></input>
							</div>
							<div className='edit-cost'>
								<label>Cost</label>
								<input id='cost' name='cost' value={cost} onChange={handleChange}></input>
							</div>
							<div className='edit-topic'>
								<label>Topic</label>
								<input id='topic' name='topic' value={topic} onChange={handleChange}></input>
							</div>
							<div className='confirm-button'>
								{editOrder && <button onClick={()=>editOrder(order.id,title,description,+cost,topic)}>Confirm</button>}
							</div>
						</form>
					</div>
				}
				
				/>} */}
				
			</div>
		</div>
	)
}

export default OrderItem
