import React, { useEffect, useState } from "react";
import "./MyOrders.css"
import { useDeleteOrderMutation, useGetProductsQuery, useGetUserProductsQuery } from "../store/order/order.api";
import { useLocation, useNavigate } from "react-router-dom";
import { IOrder } from "../store/order/order.type";
import useActions from "../hooks/useActions";
import { useTypedSelector } from "../hooks/useTypedSelector";
import OrderItem from "../components/OrderItem";
import { Pagination } from "../components/Pagination";
import { EditOrder } from "../components/EditOrder";
import { Modal, Button, OverlayTrigger, Tooltip } from 'react-bootstrap';
const initialState:IOrder[]=[]
export function MyOrders() {
    const location = useLocation();
    const pageNumber = !isNaN(Number(location.search.split("=")[1]))
        ? Number(location.search.split("=")[1])
        : 1;
    const [showEdit,setShowEdit]=useState(false);
    const handleShow = () => setShowEdit(true);
    const handleClose = () => setShowEdit(false);
    const [pagesCount, setPagesCount] = useState(0);
    const [currentPage, setCurrentPage] = useState(pageNumber);
    const navigate = useNavigate();
    const { addOrders } = useActions();
    const { auth } = useTypedSelector(state => state);
    const [deleteOrder,{isSuccess}]=useDeleteOrderMutation()
    const [orders,setOrders]=useState(initialState)
    const { data, isLoading, error } = useGetUserProductsQuery({ page: currentPage, userId: auth.id as number })
    const handlePageChange = ({ selected }: { selected: number }) => {
        setCurrentPage(selected + 1);
        console.log(pageNumber)
        console.log("changing")
        // history.replace(`?page=${selected + 1}`);
        navigate(`/myorders/?page=${selected + 1}`)
    };
    const deleteOrderHandler=(id:number)=>{
        deleteOrder(id);
        setOrders(prev=>
            prev.filter(item=>item.id!==id))
    }
    useEffect(() => {
        if (!isLoading) {
            var total = 0;
            var perPage = 0;
            if (data?.totalCountItems)
                total = +data.totalCountItems
            if (data?.itemsPerPage)
                perPage = +data.itemsPerPage
            const pagesQuantity = Math.max(Math.ceil(total / perPage), 1)
            console.log(total + "total")
            console.log(perPage + "perpage")
            console.log(data + "data")
            if (data) {
                setOrders(data.orders as IOrder[])
            }
            if (pagesQuantity) {
                setPagesCount(pagesQuantity);
            }
            console.log(pagesQuantity + "pagesquant")
            console.log(currentPage + "currentPage")
        }
    }, [currentPage, isLoading, data,orders])
    // empty dependency array means this effect will only run once (like componentDidMount in classes)
    return (
        <div className="orders-wrapper">
            <div className="orders-container">
                {isLoading ? (
                    'Loading...'
                ) : error ? (
                    <div>Error</div>
                ) : (
                    <div className='flex flex-wrap justify-between'>
                        {data?.orders && data.orders.map(order => (
                            // (categories.length == 0 || categories.includes(order.title)) &&
                            <OrderItem key={order.id} order={order} auth={auth} deleteOrder={deleteOrder}/>
                        ))}
                    </div>
                )}
                <div className="pagination-wrapper">
                    <Pagination
                        // initialPage={1}
                        pagesCount={pagesCount}
                        onChange={handlePageChange} />
                </div>
                <div>
                <Modal show={showEdit} onHide={handleClose}>
        <Modal.Header closeButton>
            <Modal.Title>
                Edit Employee
            </Modal.Title>
        </Modal.Header>
        <Modal.Body>
            <EditOrder />
        </Modal.Body>
        <Modal.Footer>
                <Button variant="secondary" onClick={handleClose}>
                    Close Button
                </Button>
        </Modal.Footer>
    </Modal>
                </div>
            </div>
        </div>
    )
}
