import { build } from "@reduxjs/toolkit/dist/query/core/buildMiddleware/cacheLifecycle"
import {BaseQueryFn, createApi, FetchArgs, fetchBaseQuery} from "@reduxjs/toolkit/query/react"
import { IOrder } from "./order.type"

export interface IFake{
    id:number,
    idus:number,
    title:string,
    body:string,
}
export const productApi=createApi({
    reducerPath:"api/orders",
    baseQuery:fetchBaseQuery({baseUrl:'https://localhost:7197'}),
    endpoints:build=>({
        getProducts:build.query<IOrder[],number>({
            // query:(count:number)=>`Orders/GetAllOrders`
            query:(count:number)=>({
                url:`Orders/GetAllOrders`,
                responseHandler:(response)=>response.text()
            })
        }),
        getString:build.query<string,number>({
            query:(count:number)=>({
                url:`Orders/GetAllOrdersstring`,
                responseHandler:(response)=>response.text()
            })
        }),
    }
    ),
})



