import React, { useEffect, useState } from 'react'
import { Navigate, useNavigate } from 'react-router-dom'
import { toast } from 'react-toastify'
import useActions from '../hooks/useActions'
import { useLoginUserMutation } from '../store/auth/auth.api'
import "./LoginForm.css"
import { useTypedSelector } from "../hooks/useTypedSelector";
export function LoginForm() {
  const initialState = {
    login: "",
    password: "",
  }
  const navigate=useNavigate();
  const [formValue, setFormValue] = useState(initialState)
  const { login, password } = formValue
  const [loginUser,{data,isSuccess,isError,error}]=useLoginUserMutation();
  const {setUser}=useActions();
  const {auth}=useTypedSelector(state=>state);
  console.log(auth)
  const [isSucess2,setisSucess2]=useState(false);
  const handleLogin=async ()=>{
    if(login && password){
        //await loginUser({login,password})
        //console.log(data+ " auth data")
        setisSucess2((prev)=>!prev)
        console.log(isSucess2)
    }
    else{
      toast.error("Fill all fields")
    }
  }

  const handleChange=(e:any)=>{
    console.log(formValue)
    setFormValue({...formValue,[e.target.name]:e.target.value})
  }
  const handleSubmit=(e:any)=>{
    console.log(formValue)
  }
  useEffect(()=>{
    if(isSucess2){
      console.log("true")
    }
  },[isSucess2])
  return (
    <div className='login-wrapper'>
      <div className='login-form'>
        <div className='credentials'>
          <input type="login"
                  id='login'
                  className='form-control'
                  name="login"
                  value={login}
                  onChange={handleChange}
            >
          </input>
          <input type="password"
                  id='password'
                  className='form-control'
                  name="password"
                  value={password}
                  onChange={handleChange}
            >
          </input>
          <input type="submit"
                  value="Войти"
                  onClick={handleLogin}>
                  
          </input>
        </div>
        <div className='addition-buttons'>
              <a className="reg-href" >Регистрация</a>
        </div>
      </div>

    </div>
  )
}
