import React, { useEffect,useState } from "react";
import { useGetProductsQuery,useGetStringQuery } from "../store/order/order.api";
import OrderItem from "../components/OrderItem"
import './AllOrders.css'
import isNil from "lodash/isNil";
import isNaN from "lodash/isNaN";
import {Link, Outlet} from "react-router-dom"
import { IOrder, orderType } from "../store/order/order.type";
import { useActions } from "../hooks/useActions";
import { useTypedSelector } from "../hooks/useTypedSelector";
import { Pagination } from "../components/Pagination";
import { useLocation,useNavigate } from "react-router-dom";

export interface IFake{
    id:number,
    idus:number,
    title:string,
    body:string,
}
export function AllOrders() {
    const { data, isLoading, error } = useGetProductsQuery(10)
    // const {data,isLoading,error}=useGetStringQuery(10);
    console.log( data +" here working");
    const data2:IOrder[]=[
        {
            id:1,
    title:"asda",
    description:"",
    cost:1234,
    period:"asda",
        },
        {
            id:2,
    title:"WTFFFFa",
    description:"adasasdadasdasdda",
    cost:123422,
    period:"asda",
        },
        {
            id:3,
    title:"aAAAAAAAAAsda",
    description:"adasda",
    cost:1234123,
    period:"asda",
        },
        {
            id:4,
    title:"asda",
    description:"adasda",
    cost:12,
    period:"asda",
        }
    ]
    const {addOrders}=useActions();
    const {orders}=useTypedSelector(state=>state);
    console.log(orders +" orders");

    const navigate = useNavigate();
    const location = useLocation();
    const pageNumber = !isNaN(Number(location.search.split("=")[1]))
    ? Number(location.search.split("=")[1])
    : 1;
    const [pagesCount, setPagesCount] = useState(0);
    const [currentPage, setCurrentPage] = useState(0);
    const handlePageChange = ({selected}:{selected:number}) => {
        setCurrentPage(selected + 1);
        // history.replace(`?page=${selected + 1}`);
        navigate(`/allOrders/?page=${selected + 1}`)
      };
    // const [isLoading,error]:boolean[]=[false,true]
    const testhandle=()=>{
        console.log(currentPage)
    }
    useEffect(()=>{
        if(data){
            addOrders(data as IOrder[])
        }
    },[])
    return (
        <div className="all-container">
            <div className="left-container">
                Left
                <div className="filter-container">
                    <div className="filter-header">
                        Выбрать критерии
                    </div>
                    <ul>
                        <li></li>
                    </ul>
                </div>  
            </div>
            {/* <div className='head-container'>
                <h3>Все доступные заказы</h3>
            </div> */}
            <div className="orders-container">
                <h4 className="orders-header">Все заказы</h4>
                {isLoading ? (
                    'Loading...'
                ) : !error ? (
                    <div>Error</div>
                ) : (
                    <div className='flex flex-wrap justify-between'>
                        {data2 && data2.map(order => (
                            <OrderItem key={order.id} order={order} />
                        ))}
                    </div>
                )}
                <Pagination
            initialPage={1}
            pagesCount={3}
            onChange={handlePageChange}/>
            <button onClick={testhandle}>click</button>
            </div>
            
        </div>
    )
}
