import React from 'react'
import { FC,useState } from 'react'
import { IOrder } from "../store/order/order.type"
import "./OrderItem.css"
import { Link, Outlet } from "react-router-dom"
import { EditModal } from './EditModal'
import { Modal } from './Modal'
// const OrderItem: FC<{ order: IOrder }> = ({ order }) => {

export interface IOrderItemProps{
	order:IOrder,
	auth?:{id:number |null;
		name:string | null;
		token:string | null;
	}
	deleteOrder?:(id:number)=>void;
	editOrder?:()=>void;
}

const OrderItem: FC<IOrderItemProps> = ({ order,auth,deleteOrder,editOrder }) => {
	const [editModal,setEditModal]=useState(false)
	const closemodal=()=>{
		setEditModal(false)
	}
	return (
		<div
			className='order-container'
		>
			<div className="order-content">
				<div className='title-cost'>
					<Link to={`/allorders/${order.id}`} className="nav-link">
						<h3>{order.title}</h3></Link>
					<h3>{order.cost}</h3>
				</div>
				<div className="order-description">
					<p>{order.description}</p>
				</div>
				<div className="order-credentials">
					<div className='order-topic'>{order.topic}</div>
					<div className='order-date'>{order.cost}</div>
				</div>
				{auth && deleteOrder && <div className='crud-buttons'>
					<button className="order-delete-button" onClick={()=>deleteOrder(order.id)}>Delete</button>
					<button className="order-edit-button" onClick={()=>setEditModal(true)}	>Edit</button>
				</div>}
				{/* {editModal && <EditModal showEdit={editModal} closeModal={closemodal}/>} */}
				{editModal && <Modal modalActive={editModal} setModalActive={setEditModal}
				
				children={
					<>
					<label>Title</label>
					<input>Title</input>
					</>
				}
				/>}
			</div>
		</div>
	)
}

export default OrderItem
